mapper.select:
SELECT count(*) 
FROM cart 
WHERE
	user_id = #{user_id} 

	AND product_no=#{productNo}

mapper.update:
UPDATE cart 
SET cart_cnt = cart_cnt + #{cartCnt}, 
	cart_amt = cart_amt + #{cartAmt}
WHERE user_id = #{user_id} AND product_no=#{productNo}

mapper.delete:
DELETE FROM cart 
WHERE 
	user_id = #{user_id} 

	And cart_no=#{cartNo}
